import React, { useState } from 'react'
import auth0 from '../lib/auth0'

const CreateStatus = () => {
    const [dados, setDatos] = useState({
        status: 'well',
        coords: {
            lat: null,
            long: null
        }
    })
    const getMyLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(position => {
                console.log(position.coords)
                setDatos(old => {
                    return {
                        ...old,
                    }
                })
            })
        }
    }

    return (
        <div>
            <h1>Create Status</h1>
            <label className='block'><input type="radio" name='status' value='well'/>I am fine and with no symptoms.</label>
            <label className='block'><input type="radio" name='status' value='flu'/>I have flu / cold symptoms.</label>
            <label className='block'><input type="radio" name='status' value='covid'/>I have COVID-19 symptoms.</label>
            Your current position: {JSON.stringify(dados)}
            <button onClick={getMyLocation}>Get my location</button>
        </div>
    ) 
}

export default CreateStatus

export async function getServerSideProps({ req, res }) {
    const session = await auth0.getSession(req)
    if (session) {
        return {
            props: {
                isAuth: true,
                user: session.user,
            }
        }
    }
    return {
        props: {
            isAuth: false,
            user: {}
        }

    }  
}
